import React from 'react';
import NavbarCasual from './NavbarCasual';
import NavbarProfessional from './NavbarProfessional';
import { useNavigate } from 'react-router-dom';

const LayoutWithNavbar = ({ children, isProfessional }) => {
  const navigate = useNavigate();

  // Set a flag to determine which navbar to render
  const shouldRenderProfessionalNavbar = isProfessional || window.location.pathname.includes('/professional');

  // If you're on the Professional route and click the Portfolio tab, navigate to the Professional Portfolio
  const handlePortfolioClick = () => {
    if (isProfessional) {
      navigate('/professional/Portfolio');
    } else {
      navigate('/casual/Portfolio');
    }
  };

  return (
    <div>
      {shouldRenderProfessionalNavbar ? <NavbarProfessional /> : <NavbarCasual />}
      {children}
      <button onClick={handlePortfolioClick}>Go to Portfolio</button>
    </div>
  );
};

export default LayoutWithNavbar;
