import React from "react";
import { Link } from "react-router-dom";
import './ToggleButton.scss';

const CustomSwitch = ({ onToggle, isChecked }) => (
  <div className={`checkbox-wrapper-35 ${isChecked ? 'checked' : ''}`}>
    <input
      value="private"
      name="switch"
      id="switch"
      type="checkbox"
      className="switch"
      checked={isChecked}
      onChange={onToggle}
    />
    <label htmlFor="switch">
      <span className="switch-x-text">This is </span>
      <span className="switch-x-toggletext">
        <span className="switch-x-unchecked">
          <span className="switch-x-hiddenlabel">Unchecked: </span>Off
        </span>
        <span className="switch-x-checked">
          <span className="switch-x-hiddenlabel">Checked: </span>On
        </span>
      </span>
    </label>
  </div>
);

const ToggleButton = ({ to, label, isSwitch, onToggle }) => {
  if (isSwitch) {
    return <CustomSwitch onToggle={onToggle} isChecked={false} />;
  }

  return (
    <div className="toggle-button">
      <Link to={to}>{label}</Link>
    </div>
  );
};

export default ToggleButton;
