import React from 'react';
import NavbarCasual from './NavbarCasual';
import NavbarProfessional from './NavbarProfessional';
import { useNavigate, useLocation } from 'react-router-dom';

const LayoutWithNavbar = ({ children }) => {
  const navigate = useNavigate();
  const location = useLocation();

  // Determine if the route includes '/professional' or '/casual'
  const isProfessional = location.pathname.includes('/professional');
  const isCasual = location.pathname.includes('/casual');

  // If you're on the Casual route and click the Portfolio tab, navigate to the Casual Portfolio
  const handlePortfolioClick = () => {
    if (isProfessional) {
      navigate('/professional/Portfolio');
    } else {
      navigate('/casual/Portfolio');
    }
  };

  return (
    <div>
      {isProfessional ? <NavbarProfessional /> : null}
      {isCasual ? <NavbarCasual /> : null}
      {children}
      <button onClick={handlePortfolioClick}>Go to Portfolio</button>
    </div>
  );
};

export default LayoutWithNavbar;
