// src/components/Home/Home.jsx
import React, { useState } from 'react';
import { Form, Link, useLocation } from 'react-router-dom';
import './Home.css';  // Import the styles for this component
import HomeBackground from './HomeBackGround';

const Home = () => {
  const location = useLocation();
  const [view, setView] = useState(location.pathname.includes('professional') ? 'professional' : 'casual');

  const toggleView = () => {
    setView((prevView) => (prevView === 'casual' ? 'professional' : 'casual'));
  };

  const renderToggleButton = () => {
    if (location.pathname.includes('casual') || location.pathname.includes('professional')) {
      return (
        <button className="toggle-button" onClick={toggleView}>
          Switch to {view === 'casual' ? 'Professional' : 'Casual'}
        </button>
      );
    }
  };

  return (
    <div className="home-container">
      {renderToggleButton()}
      <h1>Welcome to My Portfolio</h1>
      <p>Choose your preferred view:</p>
      <div>
        <Link to="/casual">
          <button className="view-button" disabled={view === 'casual'}>
            Casual
          </button>
        </Link>
        <Link to="/professional">
          <button className="view-button" disabled={view === 'professional'}>
            Professional
          </button>
        </Link>
      </div>
    </div>
  );
};

export default Home;
