// src/components/common/ToggleButton.jsx
import React from "react";
import { Link } from "react-router-dom";
import './ToggleButton.scss';

const ToggleButton = ({ to, label, isSwitch, onToggle }) => {
    if (isSwitch) {
      return (
        <div className="toggle-button switch" onClick={onToggle}>
          <div className="knobs"></div>
          <div className="label">{label}</div>
        </div>
      );
    }
  
    return (
      <div className="toggle-button">
        <input className="checkbox blue" id="checkbox2" type="checkbox" />
        <label htmlFor="checkbox2" className="checkbox-label">
          <span className="on">{label} Casual</span>
          <span className="off">{label} Professional</span>
        </label>
      </div>
    );
  };

export default ToggleButton;
