// src/components/common/ToggleButton.jsx
import React, { useState } from "react";
import { Link } from "react-router-dom";
import './ToggleButton.css';

const ToggleButton = ({ label, isSwitch, onToggle }) => {
  const [isChecked, setChecked] = useState(false);

  const handleToggle = () => {
    setChecked((prevChecked) => {
      const newChecked = !prevChecked;
      if (onToggle) {
        onToggle(newChecked);
      }
      return newChecked;
    });
  };

  if (isSwitch) {
    return (
      <div className={`checkbox-wrapper-35 ${isChecked ? "checked" : ""}`}>
        <input
          value="private"
          name="switch"
          id="switch"
          type="checkbox"
          className="switch"
          checked={isChecked}
          onChange={handleToggle}
        />
        <label htmlFor="switch">
          <span className="switch-x-text">This is </span>
          <span className="switch-x-toggletext">
            <span className="switch-x-unchecked">
              <span className="switch-x-hiddenlabel">Unchecked: </span>Off
            </span>
            <span className="switch-x-checked">
              <span className="switch-x-hiddenlabel">Checked: </span>On
            </span>
          </span>
        </label>
      </div>
    );
  }

  // Default behavior if not a switch
  return (
    <div className="toggle-button">
      {/* Your original code for non-switch button */}
      <Link to={to}>{label}</Link>
      {label}
    </div>
  );
};

export default ToggleButton;
