import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './Portfolio.css';

const Portfolio = () => {
    const location = useLocation();
    const isProfessionalRoute = location.pathname.includes('/professional');
    const isCasualRoute = location.pathname.includes('/casual');
  
    useEffect(() => {
      console.log('Location:', location.pathname);
      console.log('isProfessionalRoute:', isProfessionalRoute);
      console.log('isCasualRoute:', isCasualRoute);
  
      if (isProfessionalRoute) {
        console.log('Professional context is active!');
        // Handle professional context logic
      } else if (isCasualRoute) {
        console.log('Casual context is active!');
        // Handle casual context logic
      } else {
        console.log('Default context is active!');
        // Handle default context logic if neither isCasual nor isProfessional
      }
    }, [location.pathname, isProfessionalRoute, isCasualRoute]);
  
    return (
      <div>
        {/* Your common portfolio content goes here */}
        <h1>Test Portfolio</h1>
      </div>
    );
  };
  

export default Portfolio;
