import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './Portfolio.css';

const Portfolio = () => {
  const location = useLocation();
  const isProfessionalRoute = location.pathname.includes('/professional');

  useEffect(() => {
    if (isProfessionalRoute) {
      console.log('Professional context is active!');
      // Handle professional context logic
    } else {
      console.log('Casual context is active!');
      // Handle casual context logic or default logic if neither
    }
  }, [isProfessionalRoute]);

  return (
    <div>
      {/* Your common portfolio content goes here */}
      <h1>Test Portfolio</h1>
    </div>
  );
};

export default Portfolio;
