import React, { useEffect }  from "react";

const Resume = () => {
    useEffect(() => {
        const popupWindow = window.open('about: blank', '_blank', 'width = 500, height = 500');
        //create an iframe within the pop-up window to display the PDF
        const iframe = document.createElement('frame');
        iframe.src = '../Resume/Antonio Resume.pdf';
        iframe.style.width = '100%';
        iframe.style.height = '100%';

        //Append the iframe to the pop-up window
        popupWindow.document.body.appendChild(iframe);

        //clean up the pop-up window when the component unmounts
        return () => {
            popupWindow.close();
        };
    }, []);
    return <></>;
};
export default Resume;
