// src/components/common/ToggleButton.jsx
import React from "react";
import { Link } from "react-router-dom";
import './ToggleButton.css';

const ToggleButton = ({ to, label, isSwitch, onToggle }) => {
  if (isSwitch) {
    return (
      <div className="toggle-button">
        <input className="checkbox" type="checkbox" id="toggleCheckbox" onChange={onToggle} />
        <label htmlFor="toggleCheckbox" className="checkbox-label">
          <span className="on">I'm on!</span>
          <span className="off">I'm off</span>
        </label>
      </div>
    );
  }

  return (
    <div className="toggle-button">
      <Link to={to}>{label}</Link>
    </div>
  );
};

export default ToggleButton;
