// src/components/Home/Home.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import HomeBackground from './HomeBackGround';
import './Home.css';

const Home = () => {
  return (
    <div className="home-container">
      <link href='https://fonts.googleapis.com/css?family=Raleway:500,400,300,200,100' rel='stylesheet' type='text/css' />

      <HomeBackground /> {/* Render the HomeBackground component */}

      <div className="content">
        <h1>Welcome to My Portfolio</h1>
        <p>Choose your preferred view:</p>
        <div>
          <Link to="/casual">
            <button>Casual</button>
          </Link>
          <Link to="/professional">
            <button>Professional</button>
          </Link>
        </div>
      </div>

      {/* Your existing background code */}
      <div className="blur blurTop">
        <canvas className="canvas" id="blurCanvasTop"></canvas>
      </div>

      <div className="blur blurBottom">
      <canvas width="1000px" height="1000px" className="canvas" id="blurCanvasBottom"></canvas>
      </div>
    </div>
  );
};

export default Home;
