// src/components/common/ToggleButton.jsx
import React from "react";
import { Link } from "react-router-dom";
import './ToggleButton.scss';

const ToggleButton = ({ to, label, isSwitch, onToggle }) => {
  if (isSwitch) {
    return (
      <div className="toggle-button" onClick={onToggle}>
        <div className="knobs"></div>
        <div className="label">{label}</div>
      </div>
    );
  }

  return (
    <><input class="checkbox blue" id="checkbox2" type="checkbox" /><label for="checkbox2" class="checkbox-label">
          <span class="on">We're both longer than that guy up there</span>
          <span class="off">It works with much longer labels too</span>
      </label><Link to={to}>{label}</Link></>
   
  );
};

export default ToggleButton;
